import React, { useEffect, useState, useRef } from 'react';
import { AppBar, Box, Toolbar, Button, Typography, Menu, MenuItem, IconButton } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircle from '@mui/icons-material/AccountCircle';
import { Link, Navigate, useNavigate, NavLink, Outlet } from 'react-router-dom';
import { linkStyle } from './../../Style/styles';
import AuthService from './../../services/auth.service';
import jwtDecode from 'jwt-decode';



const pages = [
    <NavLink to="/random" style={linkStyle}>Random</NavLink>,
    <NavLink to="/category" style={linkStyle}>Category</NavLink>,
    <NavLink to="/author" style={linkStyle}>Author</NavLink>
];
const menuItems = [
    { name: `random`, path: `/random` },
    { name: `Search By Category`, path: `/category` },
    { name: `Search By Author`, path: `/author` },
];
const COMPONENT_MAP = {
    Random: Home,
    Author: Report,
    Category: About,
    Contact: Contact,
    Post: Post
};

export default function NavBar() {
    const [authed, setAuthed] = useState(false);
    const [anchorEl, setAnchorEl] = useState(null);
    const [anchorElNav, setAnchorElNav] = useState(null);
    const [sideMenu, setSideMenu] = useState(null);
    const [greeting, setGreeting] = useState("It's a beautiful day!");
    const userRef = useRef();
    const navigate = useNavigate();
    const user = AuthService.getCurrentUser();
    useEffect(() => {
        if (user && user.token) {
            setGreeting('Welcome ' + jwtDecode(user.token).username + ', it\'s time for quotes!');
            setAuthed(true);
        }
    }, []);
    const handleMenu = (event) => {
        setAnchorEl(event.currentTarget);
    };
    const handleMenuNav = (event) => {
        console.log('menuNav');
        setSideMenu(event.currentTarget);
    }

    const handleLogout = () => {
        setAuthed(false);
        AuthService.logout();
        setGreeting("You've been logged out, until we meet again!");
        navigate('/');
    };
    const handleHome = () => {
        setAnchorEl(null);
        navigate('/');
    };
    const handleClose = () => {
        setAnchorEl(null);
    };
    const handleCloseNavMenu = () => {
        setAnchorElNav(null);
    };
    return (
        <AppBar position="static" sx={{ bgcolor: 'transparent' }}>
            <Box sx={{
                flexGrow: 1
            }}>
                <Toolbar>
                    <IconButton
                        size="medium"
                        edge="start"
                        color="inherit"
                        aria-label="menu"
                        onClick={handleMenuNav}
                        sx={{ mr: 2 }}
                    >
                        <MenuIcon />
                    </IconButton>
                    <Box>
                        <Menu
                            id="menu-appbar"
                            anchorEl={anchorElNav}
                            anchorOrigin={{
                                vertical: 'bottom',
                                horizontal: 'left',
                            }}
                            keepMounted
                            transformOrigin={{
                                vertical: 'top',
                                horizontal: 'left',
                            }}
                            open={Boolean(anchorElNav)}
                            onClose={handleCloseNavMenu}
                            sx={{
                                display: { xs: 'block', md: 'none' },
                                textAlign: 'left',
                            }}
                        >
                            {authed && (<div>
                            {pages.map((page) => (
                                <MenuItem key={page} onClick={handleCloseNavMenu}>
                                    <Typography textAlign="left">{page}</Typography>
                                </MenuItem>
                            ))}
                            </div>
                            )}
                        </Menu>
                    </Box>
                    <Typography variant="h6" component="div" sx={{ flexGrow: 1, fontweight: 'bold', textShadow: '1px 1px 2px rgb(117, 31, 64), 0 0 25px rgb(255, 77, 86),0 0 5px rgb(202, 173, 77)', color: 'black', fontFamily: 'Dancing Script', fontSize: '2.5rem' }}>
                        {greeting}
                    </Typography>
                    {authed && (<div>
                        <Box sx={{ flexGrow: 10, display: { xs: 'none', md: 'flex' } }}>
                            {pages.map((page) => (
                                <Button
                                    key={page}
                                    onClick={handleCloseNavMenu}
                                    sx={{ my: 2, color: 'ivory', display: 'block' }}
                                >
                                    {page}
                                </Button>
                            ))}
                        </Box>
                    </div>
                    )}
                    <IconButton
                        size="large"
                        aria-label="account of current user"
                        aria-controls="menu-appbar"
                        aria-haspopup="true"
                        onClick={handleMenu}
                        color="inherit"
                    >
                        <AccountCircle />
                    </IconButton>
                    <Menu
                        id="menu-appbar"
                        anchorEl={anchorEl}
                        anchorOrigin={{
                            vertical: 'top',
                            horizontal: 'right',
                        }}
                        keepMounted
                        transformOrigin={{
                            vertical: 'top',
                            horizontal: 'right',
                        }}
                        open={Boolean(anchorEl)}
                        onClose={handleClose}
                    >
                        <MenuItem onClick={handleHome}>Home</MenuItem>
                        {authed && (
                            <MenuItem onClick={handleLogout}>Logout</MenuItem>)}
                    </Menu>
                </Toolbar>
            </Box>
        </AppBar>
    );
}
